import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import NotFound from "./pages/not-found";
import Home from "./pages/Home";
import Animes from "./pages/Animes";
import AnimeDetail from "./pages/AnimeDetail";
import EpisodeWatch from "./pages/EpisodeWatch";
import Discover from "./pages/Discover";
import Mangas from "./pages/Mangas";
import MangaDetail from "./pages/MangaDetail";
import ChapterReader from "./pages/ChapterReader";
import News from "./pages/News";
import Community from "./pages/Community";
import Timeline from "./pages/Timeline";
import Login from "./pages/Login";
import Register from "./pages/Register";
import SettingsPage from "./pages/Settings";
import Profile from "./pages/Profile";
import Statistics from "./pages/Statistics";
import Achievements from "./pages/Achievements";
import Header from "./components/Header";

function Router() {
  return (
    <div className="min-h-screen bg-background">
      <Switch>
        {/* Authentication pages without header */}
        <Route path="/login" component={Login} />
        <Route path="/register" component={Register} />

        {/* Main app pages with header */}
        <Route>
          <Header />
          <main className="pt-16">
            <Switch>
              <Route path="/" component={Home} />
              <Route path="/animes" component={Animes} />
              <Route path="/animes/:id" component={AnimeDetail} />
              <Route path="/animes/:animeId/episodes/:episodeNumber" component={EpisodeWatch} />
              <Route path="/descobrir" component={Discover} />
              <Route path="/timeline" component={Timeline} />
              <Route path="/mangas" component={Mangas} />
              <Route path="/mangas/:id" component={MangaDetail} />
              <Route path="/mangas/:mangaId/chapter/:chapterNumber" component={ChapterReader} />
              <Route path="/noticias" component={News} />
              <Route path="/comunidade" component={Community} />
              <Route path="/perfil" component={Profile} />
              <Route path="/estatisticas" component={Statistics} />
              <Route path="/conquistas" component={Achievements} />
              <Route path="/configuracoes" component={SettingsPage} />
              <Route component={NotFound} />
            </Switch>
          </main>
        </Route>
      </Switch>
    </div>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

