@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

/* Prevent scrollbar issues with fixed header */
html {
  overflow-x: hidden;
  scroll-behavior: smooth;
}

body {
  overflow-x: hidden;
  padding-right: 0 !important;
  overflow-y: overlay; /* Sobrepor scroll quando necess√°rio */
}

/* Remover barra de scroll padr√£o */
body::-webkit-scrollbar {
  width: 0px;
  background: transparent;
}

body::-webkit-scrollbar-thumb {
  background: transparent;
}

/* Para Firefox */
html {
  scrollbar-width: none;
}

/* Garantir que n√£o h√° scroll horizontal */
* {
  box-sizing: border-box;
}

html, body {
  max-width: 100vw;
  overflow-x: hidden;
}

/* Ensure main content doesn't interfere with fixed header */
main {
  position: relative;
  z-index: 1;
}

/* Fix for dropdown and modal z-index conflicts */
.fixed {
  position: fixed !important;
}

/* Ensure dropdowns always appear on top */
[data-radix-popper-content-wrapper] {
  z-index: 999 !important;
  position: fixed !important;
}

[role="menu"] {
  z-index: 999 !important;
  position: fixed !important;
}

/* Dropdown menu specific fix */
.dropdown-content {
  z-index: 999 !important;
  position: fixed !important;
}

/* Profile dropdown in header - specific positioning */
header [data-testid="button-profile-menu"] + [data-radix-dropdown-menu-content] {
  position: fixed !important;
  top: 60px !important;
  right: 320px !important;
  left: auto !important;
  transform: none !important;
  max-width: 200px !important;
  min-width: 180px !important;
  z-index: 9999 !important;
  box-shadow: 0 10px 38px -10px rgba(22, 23, 24, 0.35), 0 10px 20px -15px rgba(22, 23, 24, 0.2) !important;
}

/* Other dropdowns - general positioning */
[data-radix-dropdown-menu-content]:not(header [data-testid="button-profile-menu"] + *) {
  position: fixed !important;
  z-index: 999 !important;
  max-width: calc(100vw - 20px) !important;
}

/* Fix all dropdown and select positioning issues - EXCEPT header dropdowns */
[data-radix-select-content]:not(header [data-radix-dropdown-menu-content]),
[data-radix-popover-content] {
  max-width: calc(100vw - 40px) !important;
  transform: translateX(-50px) !important;
}

/* Specific fix for select components that might be too far right - EXCEPT header */
[data-radix-select-content]:not(header *) {
  transform: translateX(-30px) !important;
  max-width: 200px !important;
}

/* Timeline specific dropdowns - Ensure proper positioning */
[data-testid="year-selector"] [data-radix-select-content],
[data-testid="decade-filter"] [data-radix-select-content] {
  transform: translateX(0px) !important;
  left: auto !important;
  right: auto !important;
  position: fixed !important;
  z-index: 9999 !important;
  max-width: 180px !important;
  min-width: 120px !important;
}

/* Timeline filters dropdowns */
.timeline-filters [data-radix-select-content] {
  transform: translateX(0px) !important;
  max-width: 250px !important;
  min-width: 150px !important;
}

/* Login and Register pages responsive centering */
.min-h-screen {
  margin: 0 auto;
  max-width: 100vw;
  overflow-x: hidden;
}

@media (max-width: 640px) {
  .min-h-screen {
    padding-left: 1rem !important;
    padding-right: 1rem !important;
  }
}

/* Carousel animations */
.carousel-slide-enter {
  transform: translateX(100px);
  opacity: 0;
}

.carousel-slide-enter-active {
  transform: translateX(0);
  opacity: 1;
  transition: transform 600ms ease-out, opacity 600ms ease-out;
}

.carousel-slide-exit {
  transform: translateX(0);
  opacity: 1;
}

.carousel-slide-exit-active {
  transform: translateX(-100px);
  opacity: 0;
  transition: transform 600ms ease-out, opacity 600ms ease-out;
}

.carousel-image-zoom {
  transition: transform 600ms cubic-bezier(0.4, 0, 0.2, 1), opacity 600ms ease-out;
}

.carousel-image-zoom:hover {
  transform: scale(1.05);
}

.carousel-content-slide {
  transition: transform 600ms cubic-bezier(0.25, 0.46, 0.45, 0.94), opacity 600ms ease-out;
}

/* Enhanced carousel animations */
@keyframes carousel-slide-in-right {
  from {
    transform: translateX(50px);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

@keyframes carousel-slide-in-left {
  from {
    transform: translateX(-50px);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

@keyframes carousel-bounce {
  0%, 20%, 53%, 80%, 100% {
    transform: translate3d(0, 0, 0);
  }
  40%, 43% {
    transform: translate3d(0, -8px, 0);
  }
  70% {
    transform: translate3d(0, -4px, 0);
  }
  90% {
    transform: translate3d(0, -2px, 0);
  }
}

.carousel-animate-in-right {
  animation: carousel-slide-in-right 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

.carousel-animate-in-left {
  animation: carousel-slide-in-left 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

.carousel-bounce-effect:hover {
  animation: carousel-bounce 0.8s ease-in-out;
}

/* Advanced carousel background transitions */
@keyframes carousel-crossfade-in-right {
  from {
    opacity: 0;
    transform: translateX(50px) scale(1.1);
  }
  to {
    opacity: 1;
    transform: translateX(0) scale(1);
  }
}

@keyframes carousel-crossfade-in-left {
  from {
    opacity: 0;
    transform: translateX(-50px) scale(1.1);
  }
  to {
    opacity: 1;
    transform: translateX(0) scale(1);
  }
}

@keyframes carousel-crossfade-out-right {
  from {
    opacity: 1;
    transform: translateX(0) scale(1);
  }
  to {
    opacity: 0;
    transform: translateX(50px) scale(0.95);
  }
}

@keyframes carousel-crossfade-out-left {
  from {
    opacity: 1;
    transform: translateX(0) scale(1);
  }
  to {
    opacity: 0;
    transform: translateX(-50px) scale(0.95);
  }
}

.carousel-crossfade-in-right {
  animation: carousel-crossfade-in-right 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

.carousel-crossfade-in-left {
  animation: carousel-crossfade-in-left 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

.carousel-crossfade-out-right {
  animation: carousel-crossfade-out-right 0.6s cubic-bezier(0.55, 0.085, 0.68, 0.53);
}

.carousel-crossfade-out-left {
  animation: carousel-crossfade-out-left 0.6s cubic-bezier(0.55, 0.085, 0.68, 0.53);
}

/* Utility classes para truncar texto */
.line-clamp-1 {
  display: -webkit-box;
  -webkit-line-clamp: 1;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

.line-clamp-2 {
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

:root {
  --background: hsl(240, 8%, 4%);
  --foreground: hsl(240, 5%, 92%);
  --card: hsl(240, 8%, 8%);
  --card-foreground: hsl(240, 5%, 92%);
