client/src/components/AnimeCard.tsx.content:
import { Link } from "wouter";
import { Eye } from "lucide-react";
import type { AnimeWithProgress } from "@shared/schema";

interface AnimeCardProps {
  anime: AnimeWithProgress;
  showProgress?: boolean;
  rank?: number;
  isNew?: boolean;
  variant?: 'grid' | 'horizontal';
}

export default function AnimeCard({ anime, showProgress = false, rank, isNew = false, variant = 'grid' }: AnimeCardProps) {
  // Fun√ß√£o para formatar o n√∫mero de visualiza√ß√µes
  const formatViewCount = (count?: number | null) => {
    if (!count || count <= 0) return "N/A";
    if (count >= 1000000) return `${(count / 1000000).toFixed(1)}M`;
    if (count >= 1000) return `${(count / 1000).toFixed(0)}K`;
    return count.toString();
  };

  return (
    <Link href={`/animes/${anime.id}`}>
      <div className={`group cursor-pointer ${
        variant === 'horizontal' ? 'flex-none w-48 aspect-[3/4.5]' : 'w-full aspect-[3/4.5]'
      }`} data-testid={`card-anime-${anime.id}`}>
        <div className="bg-card rounded-2xl overflow-hidden shadow-lg hover:shadow-xl transition-all duration-300 hover:scale-105 relative h-full flex flex-col">
          {rank && (
            <div className="absolute top-2 left-2 z-10 bg-gradient-to-br from-yellow-400 via-yellow-500 to-orange-500 text-white text-xs w-7 h-7 rounded-full flex items-center justify-center font-bold shadow-lg border-2 border-white/20">
              {rank}
            </div>
          )}
          {isNew && (
            <div className="absolute top-2 left-2 z-10 bg-gradient-to-r from-[#8A2BE2] to-[#FF4DD8] text-white text-xs px-2 py-1 rounded-full font-medium">
              NOVO
            </div>
          )}

          {/* Tag de visualiza√ß√µes */}
          {anime.viewCount && (
            <div className="absolute top-2 right-2 z-10 bg-black/75 backdrop-blur-sm text-white text-xs px-2 py-1 rounded-full font-medium flex items-center gap-1 shadow-lg">
              <Eye className="w-3 h-3" />
              {formatViewCount(anime.viewCount)}
            </div>
          )}

          <img
            src={anime.image || "https://via.placeholder.com/400x600?text=" + encodeURIComponent(anime.title)}
            alt={anime.title}
            className="w-full h-[70%] object-cover"
            data-testid={`img-anime-${anime.id}`}
            onError={(e) => {
              const target = e.target as HTMLImageElement;
              target.src = `https://via.placeholder.com/400x600/8A2BE2/FFFFFF?text=${encodeURIComponent(anime.title.slice(0, 20))}`;
            }}
          />

          <div className="p-3 h-[30%] flex flex-col justify-between">
            <h4 className="font-semibold text-sm line-clamp-2 leading-tight" data-testid={`text-anime-title-${anime.id}`} title={anime.title}>
              {anime.title}
            </h4>

            {showProgress && anime.progress ? (
              <div className="mt-auto">
                <p className="text-xs text-muted-foreground mb-2" data-testid={`text-progress-${anime.id}`}>
                  Epis√≥dio {anime.progress.episodeNumber} de {anime.totalEpisodes}
                </p>
                <div className="w-full bg-muted rounded-full h-1">
                  <div
                    className="bg-gradient-to-r from-[#8A2BE2] to-[#FF4DD8] h-1 rounded-full transition-all duration-300"
                    style={{ width: `${anime.progress.progressPercent}%` }}
                    data-testid={`progress-bar-${anime.id}`}
                  />
                </div>
              </div>
            ) : (
              <p className="text-xs text-muted-foreground line-clamp-1 mt-auto" data-testid={`text-anime-info-${anime.id}`}>
                {anime.genres?.slice(0, 2).join(" ‚Ä¢ ")} {anime.totalEpisodes > 0 ? `‚Ä¢ ${anime.totalEpisodes} eps` : ''}
              </p>
            )}
          </div>
        </div>
      </div>
    </Link>
  );
}

client/src/components/EpisodeModal.tsx.content:
import { X, Play, ChevronLeft, ChevronRight, Loader2 } from "lucide-react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { markEpisodeWatchedFromPlayer, showAnimeCompletionModal } from "@/lib/api";
import { useState, useRef, useEffect } from "react";
import { getEpisodeVideoUrl } from "@/lib/scrapingApi";
import type { Episode } from "@shared/schema";

interface EpisodeModalProps {
  episode: Episode | null;
  isOpen: boolean;
  onClose: () => void;
  animeTitle?: string;
  animeImage?: string;
  animeId?: string;
  totalEpisodes?: number;
  episodes?: Episode[];
  onEpisodeChange?: (episode: Episode) => void;
}

export default function EpisodeModal({ 
  episode, 
  isOpen, 
  onClose, 
  animeTitle = "", 
  animeImage = "", 
  animeId = "", 
  totalEpisodes = 12,
  episodes = [],
  onEpisodeChange
}: EpisodeModalProps) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [videoUrl, setVideoUrl] = useState<string | null>(null);
  const [isLoadingVideo, setIsLoadingVideo] = useState(false);
  const [videoError, setVideoError] = useState<string | null>(null);
  const videoRef = useRef<HTMLVideoElement>(null);

  // Buscar URL do v√≠deo quando epis√≥dio muda
  useEffect(() => {
    if (episode && animeTitle && isOpen) {
      loadVideoUrl();
    }
  }, [episode, animeTitle, isOpen]);

  if (!episode) return null;

  const currentIndex = episodes.findIndex(ep => ep.number === episode.number);
  const hasNext = currentIndex < episodes.length - 1;

  const handleVideoEnd = async () => {
    console.log(`üé¨ Video terminou! Marcando epis√≥dio ${episode.number} como assistido...`);

    if (!animeId || !animeTitle) {
      console.error('‚ùå Dados do anime n√£o encontrados:', { animeId, animeTitle });
      return;
    }

    try {
      const result = await markEpisodeWatchedFromPlayer(
        animeId,
        episode.number,
        animeTitle,
        animeImage,
        totalEpisodes
      );

      console.log('‚úÖ Epis√≥dio marcado como assistido automaticamente!', result);

      if (result.completed) {
        console.log('üéâ Anime completado! Mostrando parab√©ns...');
        showAnimeCompletionModal(animeTitle, result.points);
      } else if (hasNext && onEpisodeChange) {
        // Auto avan√ßar para o pr√≥ximo epis√≥dio ap√≥s 2 segundos
        setTimeout(() => {
          const nextEpisode = episodes[currentIndex + 1];
          onEpisodeChange(nextEpisode);
          setIsPlaying(false);
        }, 2000);
      }
    } catch (error) {
      console.error('‚ùå Erro ao marcar epis√≥dio automaticamente:', error);
    }
  };

  const loadVideoUrl = async () => {
    if (!episode || !animeTitle) return;

    setIsLoadingVideo(true);
    setVideoError(null);

    try {
      console.log(`üé¨ Buscando v√≠deo real para: ${animeTitle} - Epis√≥dio ${episode.number}`);

      // üîç PRIMEIRA TENTATIVA: Usar endpoint direto se temos o ID do epis√≥dio
      if (episode.id && episode.id !== episode.number.toString()) {
        console.log(`üéØ Tentando buscar streaming direto para epis√≥dio ID: ${episode.id}`);
        // Endpoint ainda n√£o implementado, pulando para busca por nome
      }

      // üîç SEGUNDA TENTATIVA: Buscar por nome do anime
      console.log('üîç Buscando por nome do anime...');

      // Tentar extrair ano do t√≠tulo do anime (se houver)
      const yearMatch = animeTitle.match(/\b(19|20)\d{2}\b/);
      const year = yearMatch ? parseInt(yearMatch[0]) : undefined;

      // Buscar URL do v√≠deo diretamente
      const url = await getEpisodeVideoUrl(animeTitle, episode.number, year);

      if (url) {
        setVideoUrl(url);
        console.log(`‚úÖ URL do v√≠deo encontrada: ${url.substring(0, 50)}...`);
        console.log(`üì∫ VideoUrl state atualizado para: ${url}`);
      } else {
        console.warn('‚ö†Ô∏è Nenhuma URL de v√≠deo encontrada, usando placeholder');
        setVideoUrl('https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4');
        setVideoError('Epis√≥dio real n√£o dispon√≠vel - Usando v√≠deo de demonstra√ß√£o');
      }
    } catch (error) {
      console.error('‚ùå Erro ao buscar v√≠deo:', error);
      setVideoUrl('https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4');
      setVideoError('Erro ao buscar epis√≥dio - Usando v√≠deo de demonstra√ß√£o');
    } finally {
      setIsLoadingVideo(false);
    }
  };

  const handlePlayClick = async () => {
    if (!videoUrl && !isLoadingVideo) {
      await loadVideoUrl();
    }

    setIsPlaying(true);
    // Aguarda um pouco para o elemento de v√≠deo ser criado
    setTimeout(async () => {
      if (videoRef.current) {
        try {
          await videoRef.current.play();
          console.log('‚ñ∂Ô∏è V√≠deo iniciado automaticamente!');
        } catch (error) {
          console.log('‚ÑπÔ∏è Autoplay bloqueado pelo navegador, usu√°rio precisa clicar em play');
        }
      }
    }, 100);
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl bg-card border-border" data-testid="modal-episode">
        <DialogHeader className="border-b border-border pb-4">
          <DialogTitle className="text-lg font-semibold" data-testid="text-episode-title">
            Epis√≥dio {episode.number} - {episode.title}
          </DialogTitle>
          <button
            onClick={onClose}
            className="absolute right-4 top-4 p-2 hover:bg-muted rounded-lg transition-colors"
            data-testid="button-close-episode"
          >
            <X className="w-5 h-5" />
          </button>
        </DialogHeader>

        <div className="aspect-video bg-muted flex items-center justify-center relative rounded-lg overflow-hidden">
          {!isPlaying ? (
            <>
              <img
                src={episode.thumbnail || "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=800&h=450&fit=crop"}
                alt={episode.title}
                className="w-full h-full object-cover"
              />
              <div className="absolute inset-0 bg-black/40 flex items-center justify-center">
                <div className="text-center">
                  <button 
                    onClick={handlePlayClick}
                    disabled={isLoadingVideo}
                    className="w-20 h-20 mx-auto mb-4 bg-gradient-to-r from-[#8A2BE2] to-[#FF4DD8] rounded-full flex items-center justify-center anime-glow hover:opacity-90 transition-opacity disabled:opacity-50" 
                    data-testid="button-play-episode"
                  >
                    {isLoadingVideo ? (
                      <Loader2 className="w-8 h-8 text-white animate-spin" />
                    ) : (
                      <Play className="w-8 h-8 text-white ml-1" />
                    )}
                  </button>
                  <p className="text-white text-sm">
                    {isLoadingVideo ? 'Carregando v√≠deo...' : 'Clique para assistir o epis√≥dio'}
                  </p>
                  <p className="text-white/80 text-xs mt-1">Dura√ß√£o: {episode.duration} minutos</p>
                  {videoError && (
                    <p className="text-yellow-400 text-xs mt-2">‚ö†Ô∏è {videoError}</p>
                  )}
                  <p className="text-green-400 text-xs mt-2">‚úÖ Ser√° marcado automaticamente quando terminar</p>
                </div>
              </div>
            </>
          ) : (
            <>
              {videoUrl?.includes('youtube.com/embed') ? (
                // Player do YouTube para trailers oficiais
                <iframe
                  src={videoUrl}
                  className="w-full h-full rounded-lg"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                  title={`${episode.title} - Trailer Oficial`}
                  data-testid="youtube-player"
                  onLoad={() => {
                    console.log('‚úÖ Trailer oficial do YouTube carregado!');
                  }}
                />
              ) : (
                // Player HTML5 padr√£o para outros v√≠deos
                <video
                  ref={videoRef}
                  className="w-full h-full rounded-lg"
                  controls
                  autoPlay
                  onEnded={handleVideoEnd}
                  data-testid="video-player"
                  key={videoUrl} // Force reload when URL changes
                >
                  {videoUrl && (
                    <source 
                      src={videoUrl} 
                      type="video/mp4" 
                    />
                  )}
                  Seu navegador n√£o suporta reprodu√ß√£o de v√≠deo.
                </video>
              )}
            </>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

