// Fun√ß√£o para buscar dados completos dos animes em progresso
export async function getProgressAnimesWithDetails() {
  console.log('üîÑ Getting progress animes with full details...');
  try {
    const response = await fetch("/api/user/progress");
    if (!response.ok) {
      throw new Error(`HTTP ${response.status}`);
    }
    const progressData = await response.json();

    console.log('üìã Progress data:', progressData.length, 'animes');

    // Buscar detalhes completos de cada anime
    const animesWithDetails = await Promise.all(
      progressData.map(async (progress: any) => {
        try {
          // Use Jikan API for anime details
          const animeDetails = await getJikanAnimeById(progress.animeId.toString());

          if (animeDetails) {
            return {
              ...animeDetails,
              progress: {
                episodesWatched: progress.episodesWatched,
                totalEpisodes: progress.totalEpisodes,
                progressPercent: Math.round((progress.episodesWatched / progress.totalEpisodes) * 100),
                status: progress.status,
                updatedAt: progress.updatedAt
              }
            };
          } else {
            // Fallback com dados b√°sicos se n√£o conseguir buscar detalhes
            return {
              id: progress.animeId,
              title: `Anime ${progress.animeId}`,
              image: "https://via.placeholder.com/400x600",
              progress: {
                episodesWatched: progress.episodesWatched,
                totalEpisodes: progress.totalEpisodes,
                progressPercent: Math.round((progress.episodesWatched / progress.totalEpisodes) * 100),
                status: progress.status,
                updatedAt: progress.updatedAt
              }
            };
          }
        } catch (error) {
          console.warn(`‚ùå Erro ao buscar detalhes do anime ${progress.animeId}:`, error);
          return {
            id: progress.animeId,
            title: `Anime ${progress.animeId}`,
            image: "https://via.placeholder.com/400x600",
            progress: {
              episodesWatched: progress.episodesWatched,
              totalEpisodes: progress.totalEpisodes,
              progressPercent: Math.round((progress.episodesWatched / progress.totalEpisodes) * 100),
              status: progress.status,
              updatedAt: progress.updatedAt
            }
          };
        }
      })
    );

    console.log('‚úÖ Returning', animesWithDetails.length, 'animes with full details');
    return animesWithDetails;
  } catch (error) {
    console.error('‚ùå Error fetching progress animes with details:', error);
    return [];
  }
}

// Fun√ß√£o para buscar animes completados
export async function getCompletedAnimes() {
  console.log('üèÜ Getting completed animes...');
  try {
    const response = await fetch("/api/user/completed-animes");
