import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Search, Filter, Grid, List, ChevronLeft, ChevronRight, Globe, Tv } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import AnimeCard from "@/components/AnimeCard";
import ScrapedAnimeGrid from "@/components/ScrapedAnimeGrid";
import { getTrendingAnime } from "@/lib/api";

export default function Animes() {
  const [activeTab, setActiveTab] = useState("traditional");
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("popularity");
  const [filterByStatus, setFilterByStatus] = useState("all");
  const [filterByGenre, setFilterByGenre] = useState("all");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 30;

  // Buscar animes com base nos filtros
  const { data: animes = [], isLoading } = useQuery({
    queryKey: ["trending-animes"],
    queryFn: getTrendingAnime,
  });

  const filteredAnimes = animes.filter((anime: any) =>
    anime.title?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Pagina√ß√£o
  const totalPages = Math.ceil(filteredAnimes.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentAnimes = filteredAnimes.slice(startIndex, endIndex);

  const handlePageChange = (page: number) => {
    setCurrentPage(page);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Reset para p√°gina 1 quando a busca muda
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchQuery(e.target.value);
    setCurrentPage(1);
  };

  const genres = [
    "A√ß√£o", "Aventura", "Com√©dia", "Drama", "Fantasia", "Romance", 
    "Sci-Fi", "Slice of Life", "Sobrenatural", "Thriller", "Mist√©rio"
  ];

  const statusOptions = [
    { value: "all", label: "Todos os Status" },
    { value: "completed", label: "Completo" },
    { value: "ongoing", label: "Em Lan√ßamento" },
    { value: "upcoming", label: "Pr√≥ximos" },
  ];

  const sortOptions = [
    { value: "popularity", label: "Popularidade" },
    { value: "rating", label: "Avalia√ß√£o" },
    { value: "year", label: "Ano de Lan√ßamento" },
    { value: "title", label: "T√≠tulo A-Z" },
    { value: "episodes", label: "Epis√≥dios" },
  ];

  return (
    <div className="min-h-screen pt-20 pb-8">
      <div className="max-w-7xl mx-auto px-4 md:px-6 lg:px-8">
        {/* Header da p√°gina */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-foreground mb-2">Animes</h1>
          <p className="text-muted-foreground">
            Descubra e explore nossa vasta cole√ß√£o de animes
          </p>
        </div>

        {/* Tabs para alternar entre navega√ß√£o tradicional e scraping */}
        <Tabs value={activeTab} onValueChange={setActiveTab} className="mb-8">
          <TabsList className="grid w-full grid-cols-2 max-w-md mx-auto">
            <TabsTrigger value="traditional" className="flex items-center gap-2">
              <Tv className="w-4 h-4" />
              Cat√°logo
            </TabsTrigger>
            <TabsTrigger value="discover" className="flex items-center gap-2">
              <Globe className="w-4 h-4" />
              Descobrir
            </TabsTrigger>
          </TabsList>

          <TabsContent value="traditional" className="mt-8">

            {/* Barra de busca e filtros */}
        <Card className="mb-8">
          <CardContent className="p-6">
            <div className="space-y-4">
              {/* Busca principal */}
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <Input
                  placeholder="Buscar animes pelo t√≠tulo..."
                  value={searchQuery}
                  onChange={handleSearchChange}
                  className="pl-10 text-base"
                  data-testid="input-search-anime"
                />
              </div>

              {/* Filtros */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div>
                  <label className="text-sm font-medium text-foreground mb-2 block">
                    Ordenar por
                  </label>
                  <Select value={sortBy} onValueChange={setSortBy}>
                    <SelectTrigger data-testid="select-sort">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {sortOptions.map((option) => (
                        <SelectItem key={option.value} value={option.value}>
                          {option.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium text-foreground mb-2 block">
                    Status
                  </label>
                  <Select value={filterByStatus} onValueChange={setFilterByStatus}>
                    <SelectTrigger data-testid="select-status">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {statusOptions.map((option) => (
                        <SelectItem key={option.value} value={option.value}>
                          {option.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium text-foreground mb-2 block">
                    G√™nero
                  </label>
                  <Select value={filterByGenre} onValueChange={setFilterByGenre}>
                    <SelectTrigger data-testid="select-genre">
                      <SelectValue placeholder="Todos os g√™neros" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">Todos os g√™neros</SelectItem>
                      {genres.map((genre) => (
                        <SelectItem key={genre} value={genre.toLowerCase()}>
                          {genre}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium text-foreground mb-2 block">
                    Visualiza√ß√£o
                  </label>
                  <div className="flex gap-2">
                    <Button
                      variant={viewMode === "grid" ? "default" : "outline"}
                      size="sm"
                      onClick={() => setViewMode("grid")}
                      className="flex-1"
                      data-testid="button-grid-view"
                    >
                      <Grid className="w-4 h-4 mr-1" />
                      Grade
                    </Button>
                    <Button
                      variant={viewMode === "list" ? "default" : "outline"}
                      size="sm"
                      onClick={() => setViewMode("list")}
                      className="flex-1"
                      data-testid="button-list-view"
                    >
                      <List className="w-4 h-4 mr-1" />
                      Lista
                    </Button>
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Resultados */}
        <div className="mb-6">
          <div className="flex items-center justify-between">
            <p className="text-muted-foreground">
              {isLoading ? "Carregando..." : `${filteredAnimes.length} animes encontrados ‚Ä¢ P√°gina ${currentPage} de ${totalPages}`}
            </p>
            {searchQuery && (
              <Badge variant="secondary" className="text-sm">
                Buscando por: "{searchQuery}"
              </Badge>
            )}
          </div>
        </div>

        {/* Grid/Lista de animes */}
        {isLoading ? (
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
            {Array.from({ length: 12 }).map((_, i) => (
              <div key={i} className="space-y-2">
                <Skeleton className="aspect-[3/4] rounded-2xl" />
                <Skeleton className="h-4 w-32" />
                <Skeleton className="h-3 w-24" />
              </div>
            ))}
          </div>
        ) : viewMode === "grid" ? (
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
            {currentAnimes.map((anime: any) => (
              <AnimeCard key={anime.id} anime={anime} />
            ))}
          </div>
        ) : (
          <div className="space-y-4">
            {currentAnimes.map((anime: any) => (
              <Card key={anime.id} className="hover:shadow-lg transition-shadow">
                <CardContent className="p-4">
                  <div className="flex gap-4">
                    <img
                      src={anime.image}
                      alt={anime.title}
                      className="w-16 h-20 object-cover rounded"
                    />
                    <div className="flex-1">
                      <h3 className="font-semibold text-foreground mb-1">
                        {anime.title}
                      </h3>
                      <div className="flex gap-2 text-sm text-muted-foreground mb-2">
                        {anime.year && <span>{anime.year}</span>}
                        {anime.episodes && <span>‚Ä¢ {anime.episodes} eps</span>}
                        {anime.type && <span>‚Ä¢ {anime.type}</span>}
                      </div>
                      {anime.genres && (
                        <div className="flex gap-1 flex-wrap">
                          {anime.genres.slice(0, 3).map((genre: string) => (
                            <Badge key={genre} variant="outline" className="text-xs">
                              {genre}
                            </Badge>
                          ))}
                        </div>
                      )}
                    </div>
                    {anime.rating && (
                      <div className="text-right">
                        <div className="text-lg font-bold text-primary">
                          {typeof anime.rating === 'number' ? anime.rating.toFixed(1) : anime.rating}
                        </div>
                        <div className="text-xs text-muted-foreground">
                          ‚òÖ‚òÖ‚òÖ‚òÖ‚òÖ
                        </div>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Estado vazio */}
        {!isLoading && filteredAnimes.length === 0 && (
          <Card className="text-center py-12">
            <CardContent>
              <Search className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-foreground mb-2">
                Nenhum anime encontrado
              </h3>
              <p className="text-muted-foreground mb-4">
                Tente ajustar os filtros ou usar termos de busca diferentes
              </p>
              <Button 
                onClick={() => {
                  setSearchQuery("");
                  setFilterByStatus("all");
                  setFilterByGenre("all");
                  setCurrentPage(1);
                }}
                data-testid="button-clear-filters"
              >
                Limpar Filtros
              </Button>
            </CardContent>
          </Card>
        )}

        {/* Componente de Pagina√ß√£o */}
        {!isLoading && filteredAnimes.length > 0 && totalPages > 1 && (
          <div className="flex items-center justify-center space-x-2 mt-8">
            <Button
              variant="outline"
              size="sm"
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 1}
              className="flex items-center gap-2"
            >
              <ChevronLeft className="w-4 h-4" />
              Anterior
            </Button>

            <div className="flex items-center space-x-1">
              {/* Primeira p√°gina */}
              {currentPage > 3 && (
                <>
                  <Button
                    variant={1 === currentPage ? "default" : "outline"}
                    size="sm"
                    onClick={() => handlePageChange(1)}
                  >
                    1
                  </Button>
                  {currentPage > 4 && <span className="px-2 text-muted-foreground">...</span>}
                </>
              )}

              {/* P√°ginas ao redor da atual */}
              {Array.from({ length: totalPages }, (_, i) => i + 1)
                .filter(page => 
                  page >= Math.max(1, currentPage - 2) && 
                  page <= Math.min(totalPages, currentPage + 2)
                )
                .map(page => (
                  <Button
                    key={page}
                    variant={page === currentPage ? "default" : "outline"}
                    size="sm"
                    onClick={() => handlePageChange(page)}
                    className={page === currentPage ? "bg-primary text-primary-foreground" : ""}
                  >
                    {page}
                  </Button>
                ))}

              {/* √öltima p√°gina */}
              {currentPage < totalPages - 2 && (
                <>
                  {currentPage < totalPages - 3 && <span className="px-2 text-muted-foreground">...</span>}
                  <Button
                    variant={totalPages === currentPage ? "default" : "outline"}
                    size="sm"
                    onClick={() => handlePageChange(totalPages)}
                  >
                    {totalPages}
                  </Button>
                </>
              )}
            </div>

            <Button
              variant="outline"
              size="sm"
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages}
              className="flex items-center gap-2"
            >
              Pr√≥ximo
              <ChevronRight className="w-4 h-4" />
            </Button>
          </div>
        )}

            {/* Informa√ß√µes da p√°gina atual */}
            {!isLoading && filteredAnimes.length > 0 && (
              <div className="text-center mt-4 text-sm text-muted-foreground">
                Mostrando {startIndex + 1} a {Math.min(endIndex, filteredAnimes.length)} de {filteredAnimes.length} animes
              </div>
            )}
          </TabsContent>

          <TabsContent value="discover" className="mt-8">
            <ScrapedAnimeGrid />
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/Community.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";
import SocialPost from "@/components/SocialPost";
import ActiveUsers from "@/components/ActiveUsers";
import { getSocialPosts, getActiveUsers } from "@/lib/api";

export default function Community() {
  const { data: posts, isLoading: loadingPosts } = useQuery({
    queryKey: ["community-posts"],
    queryFn: getSocialPosts,
  });

  const { data: activeUsers, isLoading: loadingUsers } = useQuery({
    queryKey: ["community-active-users"],
    queryFn: getActiveUsers,
  });

  return (
    <div className="max-w-7xl mx-auto px-4 md:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl md:text-4xl font-bold mb-4" data-testid="text-community-title">
          Comunidade
        </h1>
        <p className="text-muted-foreground" data-testid="text-community-subtitle">
          Conecte-se com outros f√£s de anime e mang√°
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2">
          <h2 className="text-2xl font-semibold mb-6" data-testid="text-social-feed-expanded-title">
            Feed da Comunidade
          </h2>
          <div className="space-y-6">
            {loadingPosts ? (
              Array.from({ length: 5 }).map((_, i) => (
                <div key={i} className="bg-card rounded-2xl p-6">
                  <div className="flex space-x-4">
                    <Skeleton className="w-10 h-10 rounded-full" />
                    <div className="flex-1 space-y-2">
                      <Skeleton className="h-4 w-32" />
                      <Skeleton className="h-16 w-full" />
                      <Skeleton className="h-32 w-full rounded-xl" />
                    </div>
                  </div>
                </div>
              ))
            ) : (
              posts?.map((post) => (
                <SocialPost key={post.id} post={post} />
              ))
            )}
          </div>
        </div>

        {loadingUsers ? (
          <div className="space-y-6">
            <Skeleton className="h-6 w-32" />
            <div className="bg-card rounded-2xl p-6">
              <div className="space-y-4">
                {Array.from({ length: 5 }).map((_, i) => (
                  <div key={i} className="flex items-center space-x-3">
                    <Skeleton className="w-8 h-8 rounded-full" />
                    <div className="space-y-1">
                      <Skeleton className="h-3 w-16" />
                      <Skeleton className="h-3 w-12" />
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ) : (
          activeUsers && <ActiveUsers users={activeUsers} />
        )}
      </div>
    </div>
  );
}

