import { useQuery, useQueryClient } from "@tanstack/react-query";
import { useRef, useState, useEffect } from "react";
import HeroCarousel from "@/components/HeroCarousel";
import AnimeCard from "@/components/AnimeCard";
import MangaCard from "@/components/MangaCard";
import NewsCard from "@/components/NewsCard";
import SocialPost from "@/components/SocialPost";
import ActiveUsers from "@/components/ActiveUsers";
import { Skeleton } from "@/components/ui/skeleton";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { Link } from "wouter";
import {
  getContinueWatching,
  getTrendingAnime,
  getLatestAnime,
  getAnimesBySeason,
  getAvailableSeasons,
  getTopAnime,
  getLatestManga,
  getLatestNews,
  getSocialPosts,
  getActiveUsers,
} from "@/lib/api";

function ContinueWatchingSection() {
  const scrollRef = useRef<HTMLDivElement>(null);
  const queryClient = useQueryClient();

  const { data: continueAnimes, isLoading, refetch } = useQuery({
    queryKey: ['continue'],
    queryFn: async () => {
      const { getContinueWatching } = await import("@/lib/api");
      return getContinueWatching();
    },
    staleTime: 0, // Sempre considerar dados como stale para for√ßa re-fetch
    cacheTime: 0, // N√£o fazer cache dos dados
  });

  useEffect(() => {
    const handleContinueWatchingUpdate = () => {
      // For√ßar re-fetch imediato dos dados
      refetch();
    };

    // Tamb√©m escutar eventos de epis√≥dios desmarcados
    const handleEpisodeUnwatched = () => {
      // For√ßar re-fetch imediato dos dados
      refetch();
    };

    window.addEventListener('continueWatchingUpdated', handleContinueWatchingUpdate);
    window.addEventListener('episodeUnwatched', handleEpisodeUnwatched);

    return () => {
      window.removeEventListener('continueWatchingUpdated', handleContinueWatchingUpdate);
      window.removeEventListener('episodeUnwatched', handleEpisodeUnwatched);
    };
  }, [refetch]);

  const scrollLeft = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({ left: -300, behavior: 'smooth' });
    }
  };

  const scrollRight = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({ left: 300, behavior: 'smooth' });
    }
  };

  // N√£o renderizar a se√ß√£o se n√£o houver animes para continuar assistindo
  if (isLoading) {
    return (
      <div className="space-y-4">
        <h3 className="text-xl font-semibold">Continue assistindo</h3>
        <div className="flex space-x-4">
          {Array.from({ length: 4 }).map((_, i) => (
            <div key={i} className="flex-none w-48 aspect-[3/4.5]">
              <Skeleton className="w-full h-[70%] rounded-2xl mb-3" />
              <Skeleton className="h-4 w-32 mb-2" />
              <Skeleton className="h-3 w-24" />
            </div>
          ))}
        </div>
      </div>
    );
  }

  if (!continueAnimes || continueAnimes.length === 0) {
    return null; // N√£o renderiza nada se n√£o h√° progresso
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h3 className="text-xl font-semibold" data-testid="text-section-continue">Continue assistindo</h3>
        {continueAnimes && continueAnimes.length > 4 && (
          <div className="flex items-center gap-2">
            <Button
              variant="ghost"
              size="icon"
              onClick={scrollLeft}
              className="h-8 w-8 rounded-full bg-background/80 hover:bg-background border"
              data-testid="button-scroll-left-continue"
            >
              <ChevronLeft className="h-4 w-4" />
            </Button>
            <Button
              variant="ghost"
              size="icon"
              onClick={scrollRight}
              className="h-8 w-8 rounded-full bg-background/80 hover:bg-background border"
              data-testid="button-scroll-right-continue"
            >
              <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        )}
      </div>
      <div className="relative">
        <div 
          ref={scrollRef}
          className="flex space-x-4 overflow-x-auto hide-scrollbar pb-2 gradient-mask-r"
        >
          {continueAnimes.map((anime) => (
            <AnimeCard
              key={anime.id}
              anime={anime}
              showProgress={true}
              variant="horizontal"
            />
          ))}
        </div>
      </div>
    </div>
  );
}

// Componente espec√≠fico para a se√ß√£o de Lan√ßamentos com seletor de temporada
function LaunchesSection() {
  const [selectedSeason, setSelectedSeason] = useState('now');
  const scrollRef = useRef<HTMLDivElement>(null);
  const availableSeasons = getAvailableSeasons();

  const { data: animes, isLoading } = useQuery({
    queryKey: ['latest', selectedSeason],
    queryFn: () => getAnimesBySeason(selectedSeason),
  });

  const scrollLeft = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({ left: -300, behavior: 'smooth' });
    }
  };

  const scrollRight = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({ left: 300, behavior: 'smooth' });
    }
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h3 className="text-xl font-semibold" data-testid="text-section-launches">Lan√ßamentos</h3>
        <div className="flex items-center gap-4">
          <Select value={selectedSeason} onValueChange={setSelectedSeason}>
            <SelectTrigger className="w-48" data-testid="select-season">
              <SelectValue placeholder="Selecione a temporada" />
            </SelectTrigger>
            <SelectContent>
              {availableSeasons.map((season) => (
                <SelectItem key={season.value} value={season.value}>
                  {season.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
          {!isLoading && animes && animes.length > 4 && (
            <div className="flex items-center gap-2">
              <Button
                variant="ghost"
                size="icon"
                onClick={scrollLeft}
                className="h-8 w-8 rounded-full bg-background/80 hover:bg-background border"
                data-testid="button-scroll-left-launches"
              >
                <ChevronLeft className="h-4 w-4" />
              </Button>
              <Button
                variant="ghost"
                size="icon"
                onClick={scrollRight}
                className="h-8 w-8 rounded-full bg-background/80 hover:bg-background border"
                data-testid="button-scroll-right-launches"
              >
                <ChevronRight className="h-4 w-4" />
              </Button>
            </div>
          )}
        </div>
      </div>
      <div className="relative">
        <div 
          ref={scrollRef}
          className="flex space-x-4 overflow-x-auto hide-scrollbar pb-2 gradient-mask-r"
        >
          {isLoading ? (
            Array.from({ length: 4 }).map((_, i) => (
              <div key={i} className="flex-none w-48 aspect-[3/4.5]">
                <Skeleton className="w-full h-[70%] rounded-2xl mb-3" />
                <Skeleton className="h-4 w-32 mb-2" />
                <Skeleton className="h-3 w-24" />
              </div>
            ))
          ) : (
            animes?.map((anime) => (
              <AnimeCard
                key={anime.id}
                anime={anime}
                isNew={true}
                variant="horizontal"
              />
            ))
          )}
        </div>
      </div>
    </div>
  );
}

function AnimeSection({ title, queryKey, showProgress = false, showRank = false, isNew = false }: {
  title: string;
  queryKey: string;
  showProgress?: boolean;
  showRank?: boolean;
  isNew?: boolean;
}) {
  const scrollRef = useRef<HTMLDivElement>(null);
  const { data: animes, isLoading } = useQuery({
    queryKey: [queryKey],
    queryFn: async () => {
      const { getContinueWatching } = await import("@/lib/api");
      switch (queryKey) {
        case 'continue': return getContinueWatching();
        case 'trending': return getTrendingAnime();
        case 'latest': return getLatestAnime();
        case 'top': return getTopAnime();
        default: return getTrendingAnime();
      }
    },
  });

  const scrollLeft = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({ left: -300, behavior: 'smooth' });
    }
  };

  const scrollRight = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({ left: 300, behavior: 'smooth' });
    }
  };

  // Mostrar setas para "Top 10 mais assistidos", "Lan√ßamentos" e "Recomendados para voc√™"
  const showNavigation = queryKey === 'top' || queryKey === 'latest' || queryKey === 'trending';

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h3 className="text-xl font-semibold" data-testid={`text-section-${queryKey}`}>{title}</h3>
        {showNavigation && !isLoading && animes && animes.length > 4 && (
          <div className="flex items-center gap-2">
            <Button
              variant="ghost"
              size="icon"
              onClick={scrollLeft}
              className="h-8 w-8 rounded-full bg-background/80 hover:bg-background border"
              data-testid="button-scroll-left"
            >
              <ChevronLeft className="h-4 w-4" />
            </Button>
            <Button
              variant="ghost"
              size="icon"
              onClick={scrollRight}
              className="h-8 w-8 rounded-full bg-background/80 hover:bg-background border"
              data-testid="button-scroll-right"
            >
              <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        )}
      </div>
      <div className="relative">
        <div 
          ref={scrollRef}
          className="flex space-x-4 overflow-x-auto hide-scrollbar pb-2 gradient-mask-r"
        >
          {isLoading ? (
            Array.from({ length: 4 }).map((_, i) => (
              <div key={i} className="flex-none w-48 aspect-[3/4.5]">
                <Skeleton className="w-full h-[70%] rounded-2xl mb-3" />
                <Skeleton className="h-4 w-32 mb-2" />
                <Skeleton className="h-3 w-24" />
              </div>
            ))
          ) : (
            animes?.map((anime, index) => (
              <AnimeCard
                key={anime.id}
                anime={anime}
                showProgress={showProgress}
                rank={showRank ? index + 1 : undefined}
                isNew={isNew}
                variant="horizontal"
              />
            ))
          )}
        </div>
      </div>
    </div>
  );
}

