  genres?: string[] | null;
  chapters?: number;
  latestChapter?: number | null;
  type?: string;
  author?: string | null;
  synopsis?: string | null;
}

export default function Mangas() {
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("popularity");
  const [filterByStatus, setFilterByStatus] = useState("all");
  const [filterByGenre, setFilterByGenre] = useState("all");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [currentPage, setCurrentPage] = useState(1);
  const [imageErrors, setImageErrors] = useState<Record<string, number>>({});
  const itemsPerPage = 30;

  // M√∫ltiplas op√ß√µes de fallback para imagens
  const getFallbackImages = (mangaId: string, title: string) => [
    `https://picsum.photos/400/600?random=${mangaId}`,
    `https://via.placeholder.com/400x600/8A2BE2/FFFFFF?text=${encodeURIComponent(title.slice(0, 15))}`,
    `https://dummyimage.com/400x600/8A2BE2/FFFFFF&text=${encodeURIComponent(title.slice(0, 15))}`,
    `https://via.placeholder.com/400x600/FF4DD8/FFFFFF?text=Manga`
  ];

  const handleImageError = (mangaId: string) => {
    setImageErrors(prev => {
      const currentIndex = prev[mangaId] || 0;
      return { ...prev, [mangaId]: currentIndex + 1 };
    });
  };

  const getMangaImageSrc = (manga: Manga) => {
    const errorIndex = imageErrors[manga.id] || 0;
    const fallbacks = getFallbackImages(manga.id, manga.title);

    if (errorIndex === 0 && manga.image) {
      return manga.image;
    }

    const fallbackIndex = Math.min(errorIndex - 1, fallbacks.length - 1);
    return fallbacks[Math.max(0, fallbackIndex)];
  };

  // Buscar mang√°s
  const { data: mangas = [], isLoading } = useQuery({
    queryKey: ["latest-manga"],
    queryFn: getLatestManga,
  });

  const filteredMangas = mangas.filter((manga: Manga) =>
    manga.title?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Pagina√ß√£o
  const totalPages = Math.ceil(filteredMangas.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentMangas = filteredMangas.slice(startIndex, endIndex);

  const handlePageChange = (page: number) => {
    setCurrentPage(page);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Reset para p√°gina 1 quando a busca muda
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchQuery(e.target.value);
    setCurrentPage(1);
  };

  const genres = [
    "A√ß√£o", "Aventura", "Com√©dia", "Drama", "Fantasia", "Romance", 
    "Sci-Fi", "Slice of Life", "Sobrenatural", "Thriller", "Mist√©rio",
    "Horror", "Psicol√≥gico", "Escola", "Esporte"
  ];

  const statusOptions = [
    { value: "all", label: "Todos os Status" },
    { value: "completed", label: "Completo" },
    { value: "ongoing", label: "Em Lan√ßamento" },
    { value: "upcoming", label: "Pr√≥ximos" },
  ];

  const sortOptions = [
    { value: "popularity", label: "Popularidade" },
    { value: "rating", label: "Avalia√ß√£o" },
    { value: "year", label: "Ano de Lan√ßamento" },
    { value: "title", label: "T√≠tulo A-Z" },
    { value: "chapters", label: "Cap√≠tulos" },
  ];

  return (
    <div className="min-h-screen pt-20 pb-8">
      <div className="max-w-7xl mx-auto px-4 md:px-6 lg:px-8">
        {/* Header da p√°gina */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-foreground mb-2">Mang√°s</h1>
          <p className="text-muted-foreground">
            Explore nossa cole√ß√£o de mang√°s e encontre sua pr√≥xima leitura
          </p>
        </div>

        {/* Barra de busca e filtros */}
        <Card className="mb-8">
          <CardContent className="p-6">
            <div className="space-y-4">
              {/* Busca principal */}
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <Input
                  placeholder="Buscar mang√°s pelo t√≠tulo..."
                  value={searchQuery}
                  onChange={handleSearchChange}
                  className="pl-10 text-base"
                  data-testid="input-search-manga"
                />
              </div>

              {/* Filtros */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div>
                  <label className="text-sm font-medium text-foreground mb-2 block">
                    Ordenar por
                  </label>
                  <Select value={sortBy} onValueChange={setSortBy}>
                    <SelectTrigger data-testid="select-sort">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {sortOptions.map((option) => (
                        <SelectItem key={option.value} value={option.value}>
                          {option.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium text-foreground mb-2 block">
                    Status
                  </label>
                  <Select value={filterByStatus} onValueChange={setFilterByStatus}>
                    <SelectTrigger data-testid="select-status">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {statusOptions.map((option) => (
                        <SelectItem key={option.value} value={option.value}>
                          {option.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium text-foreground mb-2 block">
                    G√™nero
                  </label>
                  <Select value={filterByGenre} onValueChange={setFilterByGenre}>
                    <SelectTrigger data-testid="select-genre">
                      <SelectValue placeholder="Todos os g√™neros" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">Todos os g√™neros</SelectItem>
                      {genres.map((genre) => (
                        <SelectItem key={genre} value={genre.toLowerCase()}>
                          {genre}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium text-foreground mb-2 block">
                    Visualiza√ß√£o
                  </label>
                  <div className="flex gap-2">
                    <Button
                      variant={viewMode === "grid" ? "default" : "outline"}
                      size="sm"
                      onClick={() => setViewMode("grid")}
                      className="flex-1"
                      data-testid="button-grid-view"
                    >
                      <Grid className="w-4 h-4 mr-1" />
                      Grade
                    </Button>
                    <Button
                      variant={viewMode === "list" ? "default" : "outline"}
                      size="sm"
                      onClick={() => setViewMode("list")}
                      className="flex-1"
                      data-testid="button-list-view"
                    >
                      <List className="w-4 h-4 mr-1" />
                      Lista
                    </Button>
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Resultados */}
        <div className="mb-6">
          <div className="flex items-center justify-between">
            <p className="text-muted-foreground">
              {isLoading ? "Carregando..." : `${filteredMangas.length} mang√°s encontrados ‚Ä¢ P√°gina ${currentPage} de ${totalPages}`}
            </p>
            {searchQuery && (
              <Badge variant="secondary" className="text-sm">
                Buscando por: "{searchQuery}"
              </Badge>
            )}
          </div>
        </div>

        {/* Grid/Lista de mang√°s */}
        {isLoading ? (
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
            {Array.from({ length: 12 }).map((_, i) => (
              <div key={i} className="space-y-2">
                <Skeleton className="aspect-[3/4] rounded-2xl" />
                <Skeleton className="h-4 w-32" />
                <Skeleton className="h-3 w-24" />
              </div>
            ))}
          </div>
        ) : viewMode === "grid" ? (
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
            {currentMangas.map((manga: Manga) => (
              <Link key={manga.id} href={`/mangas/${manga.id}`}>
                <Card 
                  className="group cursor-pointer hover:shadow-lg transition-all duration-300 hover:scale-105"
                  data-testid={`card-manga-${manga.id}`}
                >
                  <CardContent className="p-0">
                    <div className="aspect-[3/4] relative overflow-hidden rounded-t-lg">
                      <img
                        src={getMangaImageSrc(manga)}
                        alt={manga.title}
                        className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
                        onError={() => handleImageError(manga.id)}
                      />
                      <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />

                      {/* Action overlay */}
                      <div className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                        <Button size="sm" className="bg-primary/90 hover:bg-primary">
                          <BookOpen className="w-4 h-4 mr-2" />
                          Ler Agora
                        </Button>
                      </div>
                    </div>

                    <div className="p-3">
                      <h3 className="font-semibold text-sm text-foreground truncate mb-1">
                        {manga.title}
                      </h3>
                      <div className="text-xs text-muted-foreground space-y-1">
                        {manga.author && (
                          <p className="truncate">Por: {manga.author}</p>
                        )}
                        <div className="flex justify-between">
                          {manga.year && <span>{manga.year}</span>}
                          {manga.latestChapter && <span>Cap. {manga.latestChapter}</span>}
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </Link>
            ))}
          </div>
        ) : (
          <div className="space-y-4">
            {currentMangas.map((manga: Manga) => (
              <Card key={manga.id} className="hover:shadow-lg transition-shadow">
                <CardContent className="p-4">
                  <div className="flex gap-4">
                    <img
                      src={getMangaImageSrc(manga)}
                      alt={manga.title}
                      className="w-16 h-20 object-cover rounded"
                      onError={() => handleImageError(manga.id)}
                    />
                    <div className="flex-1">
                      <h3 className="font-semibold text-foreground mb-1">
                        {manga.title}
                      </h3>
                      {manga.author && (
                        <p className="text-sm text-muted-foreground mb-1">
                          Por: {manga.author}
                        </p>
                      )}
                      <div className="flex gap-2 text-sm text-muted-foreground mb-2">
                        {manga.year && <span>{manga.year}</span>}
                        {manga.latestChapter && <span>‚Ä¢ Cap. {manga.latestChapter}</span>}
                        {manga.type && <span>‚Ä¢ {manga.type}</span>}
                      </div>
                      {manga.genres && (
                        <div className="flex gap-1 flex-wrap">
                          {manga.genres.slice(0, 3).map((genre: string) => (
                            <Badge key={genre} variant="outline" className="text-xs">
                              {genre}
                            </Badge>
                          ))}
                        </div>
                      )}
                    </div>
                    {manga.rating && (
                      <div className="text-right">
                        <div className="text-lg font-bold text-primary">
                          {typeof manga.rating === 'number' ? manga.rating.toFixed(1) : manga.rating}
                        </div>
                        <div className="text-xs text-muted-foreground">
                          ‚òÖ‚òÖ‚òÖ‚òÖ‚òÖ
                        </div>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Estado vazio */}
        {!isLoading && filteredMangas.length === 0 && (
          <Card className="text-center py-12">
            <CardContent>
              <BookOpen className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-foreground mb-2">
                Nenhum mang√° encontrado
              </h3>
              <p className="text-muted-foreground mb-4">
                Tente ajustar os filtros ou usar termos de busca diferentes
              </p>
              <Button 
                onClick={() => {
                  setSearchQuery("");
                  setFilterByStatus("all");
                  setFilterByGenre("all");
                  setCurrentPage(1);
                }}
                data-testid="button-clear-filters"
              >
                Limpar Filtros
              </Button>
            </CardContent>
          </Card>
        )}

        {/* Componente de Pagina√ß√£o */}
        {!isLoading && filteredMangas.length > 0 && totalPages > 1 && (
          <div className="flex items-center justify-center space-x-2 mt-8">
            <Button
              variant="outline"
              size="sm"
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 1}
              className="flex items-center gap-2"
            >
              <ChevronLeft className="w-4 h-4" />
              Anterior
            </Button>

            <div className="flex items-center space-x-1">
              {/* Primeira p√°gina */}
              {currentPage > 3 && (
                <>
                  <Button
                    variant={1 === currentPage ? "default" : "outline"}
                    size="sm"
                    onClick={() => handlePageChange(1)}
                  >
                    1
                  </Button>
                  {currentPage > 4 && <span className="px-2 text-muted-foreground">...</span>}
                </>
              )}

              {/* P√°ginas ao redor da atual */}
              {Array.from({ length: totalPages }, (_, i) => i + 1)
                .filter(page => 
                  page >= Math.max(1, currentPage - 2) && 
                  page <= Math.min(totalPages, currentPage + 2)
                )
                .map(page => (
                  <Button
                    key={page}
                    variant={page === currentPage ? "default" : "outline"}
                    size="sm"
                    onClick={() => handlePageChange(page)}
                    className={page === currentPage ? "bg-primary text-primary-foreground" : ""}
                  >
                    {page}
                  </Button>
                ))}

              {/* √öltima p√°gina */}
              {currentPage < totalPages - 2 && (
                <>
                  {currentPage < totalPages - 3 && <span className="px-2 text-muted-foreground">...</span>}
                  <Button
                    variant={totalPages === currentPage ? "default" : "outline"}
                    size="sm"
                    onClick={() => handlePageChange(totalPages)}
                  >
                    {totalPages}
                  </Button>
                </>
              )}
            </div>

            <Button
              variant="outline"
              size="sm"
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages}
              className="flex items-center gap-2"
            >
              Pr√≥ximo
              <ChevronRight className="w-4 h-4" />
            </Button>
          </div>
        )}

        {/* Informa√ß√µes da p√°gina atual */}
        {!isLoading && filteredMangas.length > 0 && (
          <div className="text-center mt-4 text-sm text-muted-foreground">
            Mostrando {startIndex + 1} a {Math.min(endIndex, filteredMangas.length)} de {filteredMangas.length} mang√°s
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/News.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import NewsCard from "@/components/NewsCard";
import NewsModal from "@/components/NewsModal";
import CreateNewsModal from "@/components/CreateNewsModal";
import { getLatestNews, getNewsByCategory } from "@/lib/api";
import { Newspaper, TrendingUp, Star, Film, Plus } from "lucide-react";

interface NewsItem {
  id: string;
  title: string;
  description: string;
  link: string;
  publishedDate: string;
  category?: string;
  thumbnail?: string;
  author?: string;
}

const newsCategories = [
  { key: 'all', label: 'Todas', icon: Newspaper },
  { key: 'news', label: 'Not√≠cias', icon: TrendingUp },
  { key: 'reviews', label: 'Reviews', icon: Star },
  { key: 'features', label: 'Especiais', icon: Film },
];

export default function News() {
  const [activeCategory, setActiveCategory] = useState('all');
  const [selectedNews, setSelectedNews] = useState<NewsItem | null>(null);
  const [isNewsModalOpen, setIsNewsModalOpen] = useState(false);
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);

  const { data: news, isLoading, error } = useQuery({
    queryKey: ["anime-news", activeCategory],
    queryFn: () => {
      if (activeCategory === 'all') {
        return getLatestNews();
      }
      return getNewsByCategory(activeCategory);
    },
    retry: 3,
    retryDelay: 1000,
  });

  return (
    <div className="max-w-7xl mx-auto px-4 md:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-3">
            <Newspaper className="h-8 w-8 text-primary" />
            <h1 className="text-3xl md:text-4xl font-bold" data-testid="text-news-page-title">
              Not√≠cias de Anime
            </h1>
          </div>
          <Button
            onClick={() => setIsCreateModalOpen(true)}
            className="flex items-center gap-2 bg-purple-600 hover:bg-purple-700"
            data-testid="button-create-news"
          >
            <Plus className="w-4 h-4" />
            Criar Not√≠cia
          </Button>
        </div>
        <p className="text-muted-foreground mb-6" data-testid="text-news-page-subtitle">
          As √∫ltimas not√≠cias direto do Anime News Network - Fique por dentro de tudo que acontece no mundo dos animes
        </p>

        {/* Filtros por categoria */}
        <div className="flex flex-wrap gap-2 mb-6">
          {newsCategories.map((category) => {
            const IconComponent = category.icon;
            return (
              <Button
                key={category.key}
                variant={activeCategory === category.key ? "default" : "outline"}
                size="sm"
                onClick={() => setActiveCategory(category.key)}
                className="flex items-center gap-2"
                data-testid={`button-filter-${category.key}`}
              >
                <IconComponent className="h-4 w-4" />
                {category.label}
              </Button>
            );
          })}
        </div>

        {/* Indicador da fonte */}
        <div className="flex items-center gap-2 mb-6">
          <Badge variant="secondary" className="text-xs">
            Fonte: Anime News Network
          </Badge>
          <Badge variant="outline" className="text-xs">
            {news?.length || 0} not√≠cias
          </Badge>
        </div>
      </div>

      {/* Estado de erro */}
      {error && (
        <div className="text-center py-12">
          <div className="text-muted-foreground mb-4">
            Erro ao carregar not√≠cias. Usando dados de fallback.
          </div>
        </div>
      )}

      {/* Loading skeleton */}
      {isLoading ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {Array.from({ length: 12 }).map((_, i) => (
            <div key={i} className="space-y-3">
              <Skeleton className="aspect-[3/2] rounded-2xl" />
              <Skeleton className="h-4 w-full" />
              <Skeleton className="h-3 w-3/4" />
              <div className="flex justify-between">
                <Skeleton className="h-3 w-16" />
                <Skeleton className="h-3 w-20" />
              </div>
            </div>
          ))}
        </div>
      ) : (
        <>
          {/* Grid de not√≠cias */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {news?.map((newsItem) => {
              // Convert API response to NewsItem format
              const newsItemFormatted: NewsItem = {
                id: newsItem.id,
                title: newsItem.title,
                description: newsItem.description || newsItem.summary || '',
                link: newsItem.link || '#',
                publishedDate: newsItem.publishedDate || new Date().toISOString(),
                category: newsItem.category,
                thumbnail: newsItem.thumbnail || newsItem.image,
                author: newsItem.author
              };

              return (
                <NewsCard 
                  key={newsItem.id} 
                  news={newsItemFormatted}
                  onClick={(news) => {
                    setSelectedNews(news);
                    setIsNewsModalOpen(true);
                  }}
                />
              );
            })}
          </div>

          {/* Mensagem quando n√£o h√° not√≠cias */}
          {(!news || news.length === 0) && !isLoading && (
            <div className="text-center py-12">
              <Newspaper className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-semibold mb-2">
                Nenhuma not√≠cia encontrada
              </h3>
              <p className="text-muted-foreground">
                N√£o h√° not√≠cias dispon√≠veis para esta categoria no momento.
              </p>
            </div>
          )}
        </>
      )}

      {/* Modals */}
      <NewsModal
        news={selectedNews}
        isOpen={isNewsModalOpen}
        onClose={() => {
          setIsNewsModalOpen(false);
          setSelectedNews(null);
        }}
      />

      <CreateNewsModal
        isOpen={isCreateModalOpen}
        onClose={() => setIsCreateModalOpen(false)}
        onNewsCreated={(newNews: NewsItem) => {
          // Aqui voc√™ pode atualizar a lista de not√≠cias ou fazer refresh
          console.log('Nova not√≠cia criada:', newNews);
          setIsCreateModalOpen(false);
        }}
      />
    </div>
  );
}

