                <div className="space-y-2">
                  <Skeleton className="h-4 w-full" />
                  <Skeleton className="h-4 w-5/6" />
                  <Skeleton className="h-4 w-4/5" />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <Skeleton className="h-16" />
                <Skeleton className="h-16" />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!manga) {
    return (
      <div className="min-h-screen pt-20 pb-8 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-foreground mb-2">Mang√° n√£o encontrado</h1>
          <p className="text-muted-foreground mb-6">O mang√° que voc√™ est√° procurando n√£o existe ou foi removido.</p>
          <Link href="/mangas">
            <Button>
              <ArrowLeft className="w-4 h-4 mr-2" />
              Voltar para Mang√°s
            </Button>
