            {/* G√™neros */}
            {manga.genres && manga.genres.length > 0 && (
              <div>
                <h3 className="text-lg font-semibold text-foreground mb-3">G√™neros</h3>
                <div className="flex flex-wrap gap-2">
                  {manga.genres.map((genre: string) => (
                    <Badge key={genre} variant="outline" className="text-sm">
                      {genre}
                    </Badge>
                  ))}
                </div>
              </div>
            )}

            {/* Sinopse */}
            <div>
              <h3 className="text-lg font-semibold text-foreground mb-3">Sinopse</h3>
              <div className="prose prose-sm max-w-none text-muted-foreground">
                <p>
                  {manga.synopsis ? (
                    isExpanded ? 
                      manga.synopsis : 
                      truncateSynopsis(manga.synopsis)
                  ) : (
                    "Sinopse n√£o dispon√≠vel para este mang√°."
                  )}
                </p>

                {manga.synopsis && shouldShowReadMore(manga.synopsis) && (
                  <Button
                    variant="ghost"
                    size="sm"
