import { useState } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useToast } from '@/hooks/use-toast';
import { X, Upload, Image as ImageIcon } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';

interface NewsItem {
  id: string;
  title: string;
  description: string;
  link: string;
  publishedDate: string;
  category?: string;
  thumbnail?: string;
  author?: string;
}

interface CreateNewsModalProps {
  isOpen: boolean;
  onClose: () => void;
  onNewsCreated?: (news: NewsItem) => void;
}

export default function CreateNewsModal({ isOpen, onClose, onNewsCreated }: CreateNewsModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    imageUrl: '',
    author: ''
  });

  const createNewsMutation = useMutation({
    mutationFn: async (newsData: typeof formData) => {
      const response = await fetch('/api/news/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          title: newsData.title,
          description: newsData.description,
          category: newsData.category || 'news',
          thumbnail: newsData.imageUrl,
          author: newsData.author || 'AnimePulse',
          link: '#',
          publishedDate: new Date().toISOString()
        })
      });

      if (!response.ok) {
        throw new Error('Failed to create news');
      }

      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Not√≠cia criada com sucesso!",
        description: "Sua not√≠cia foi publicada e j√° est√° dispon√≠vel para visualiza√ß√£o."
      });

      // Invalidar cache de not√≠cias para atualizar a lista
      queryClient.invalidateQueries({ queryKey: ['anime-news'] });

      // Callback para parent component
      if (onNewsCreated) {
        onNewsCreated(data);
      }

      // Reset form
      setFormData({
        title: '',
        description: '',
        category: '',
        imageUrl: '',
        author: ''
      });

      onClose();
    },
    onError: (error) => {
      console.error('Error creating news:', error);
      toast({
        title: "Erro ao criar not√≠cia",
        description: "Houve um problema ao publicar sua not√≠cia. Tente novamente.",
        variant: "destructive"
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.title.trim() || !formData.description.trim()) {
      toast({
        title: "Campos obrigat√≥rios",
        description: "Por favor, preencha pelo menos o t√≠tulo e a descri√ß√£o.",
        variant: "destructive"
      });
      return;
    }

    createNewsMutation.mutate(formData);
  };

  const handleClose = () => {
    if (!createNewsMutation.isPending) {
      setFormData({
        title: '',
        description: '',
        category: '',
        imageUrl: '',
        author: ''
      });
      onClose();
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-hidden flex flex-col">
        <DialogHeader className="flex-shrink-0">
          <div className="flex items-center justify-between">
            <DialogTitle className="text-xl">Criar Nova Not√≠cia</DialogTitle>
            <Button
              variant="ghost"
              size="icon"
              onClick={handleClose}
              disabled={createNewsMutation.isPending}
              data-testid="button-close-create-news-modal"
            >
              <X className="w-4 h-4" />
            </Button>
          </div>
          <p className="text-sm text-muted-foreground">
            Compartilhe as √∫ltimas novidades do mundo dos animes
          </p>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="flex-1 overflow-y-auto space-y-6 mt-6">
          {/* T√≠tulo */}
          <div className="space-y-2">
            <Label htmlFor="title">T√≠tulo *</Label>
            <Input
              id="title"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              placeholder="Digite o t√≠tulo da not√≠cia..."
              required
              disabled={createNewsMutation.isPending}
              data-testid="input-news-title"
            />
          </div>

          {/* Categoria */}
          <div className="space-y-2">
            <Label htmlFor="category">Categoria</Label>
            <Select
              value={formData.category}
              onValueChange={(value) => setFormData(prev => ({ ...prev, category: value }))}
              disabled={createNewsMutation.isPending}
            >
              <SelectTrigger data-testid="select-news-category">
                <SelectValue placeholder="Selecione uma categoria" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="news">Not√≠cias</SelectItem>
                <SelectItem value="reviews">Reviews</SelectItem>
                <SelectItem value="features">Especiais</SelectItem>
                <SelectItem value="releases">Lan√ßamentos</SelectItem>
                <SelectItem value="industry">Ind√∫stria</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Autor */}
          <div className="space-y-2">
            <Label htmlFor="author">Autor</Label>
            <Input
              id="author"
              value={formData.author}
              onChange={(e) => setFormData(prev => ({ ...prev, author: e.target.value }))}
              placeholder="Nome do autor (opcional)"
              disabled={createNewsMutation.isPending}
              data-testid="input-news-author"
            />
          </div>

          {/* URL da Imagem */}
          <div className="space-y-2">
            <Label htmlFor="imageUrl">URL da Imagem</Label>
            <div className="flex gap-2">
              <Input
                id="imageUrl"
                value={formData.imageUrl}
                onChange={(e) => setFormData(prev => ({ ...prev, imageUrl: e.target.value }))}
                placeholder="https://exemplo.com/imagem.jpg"
                disabled={createNewsMutation.isPending}
                data-testid="input-news-image-url"
              />
              <Button
                type="button"
                variant="outline"
                size="icon"
                disabled={createNewsMutation.isPending}
                data-testid="button-upload-image"
              >
                <ImageIcon className="w-4 h-4" />
              </Button>
            </div>
            {formData.imageUrl && (
              <div className="mt-2">
                <img
                  src={formData.imageUrl}
                  alt="Preview"
                  className="w-full max-h-32 object-cover rounded border"
                  onError={(e) => {
                    const target = e.target as HTMLImageElement;
                    target.style.display = 'none';
                  }}
                />
              </div>
            )}
          </div>

          {/* Descri√ß√£o/Conte√∫do */}
          <div className="space-y-2">
            <Label htmlFor="description">Conte√∫do *</Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              placeholder="Escreva o conte√∫do da not√≠cia..."
              rows={8}
              required
              disabled={createNewsMutation.isPending}
              data-testid="textarea-news-description"
            />
            <p className="text-xs text-muted-foreground">
              {formData.description.length}/500 caracteres
            </p>
          </div>

          {/* Bot√µes de a√ß√£o */}
          <div className="flex justify-end gap-2 pt-4 border-t">
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
              disabled={createNewsMutation.isPending}
              data-testid="button-cancel-create-news"
            >
              Cancelar
            </Button>
            <Button
              type="submit"
              disabled={createNewsMutation.isPending || !formData.title.trim() || !formData.description.trim()}
              className="bg-purple-600 hover:bg-purple-700"
              data-testid="button-submit-create-news"
            >
              {createNewsMutation.isPending ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Publicando...
                </>
              ) : (
                <>
                  <Upload className="w-4 h-4 mr-2" />
                  Publicar Not√≠cia
                </>
              )}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/NewsModal.tsx.content:
import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Skeleton } from '@/components/ui/skeleton';
import { Calendar, User, ExternalLink, Share2, BookmarkPlus, X, Loader2 } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface NewsItem {
  id: string;
  title: string;
  description: string;
  content?: string;
  link: string;
  publishedDate: string;
  category?: string;
  thumbnail?: string;
  author?: string;
}

interface NewsModalProps {
  news: NewsItem | null;
  isOpen: boolean;
  onClose: () => void;
}

export default function NewsModal({ news, isOpen, onClose }: NewsModalProps) {
  const { toast } = useToast();
  const [isBookmarked, setIsBookmarked] = useState(false);

  // Buscar conte√∫do completo da not√≠cia
  const { data: fullNews, isLoading: loadingFullNews } = useQuery({
    queryKey: ['news-detail', news?.id],
    queryFn: async () => {
      if (!news?.id) throw new Error('News ID not provided');
      const response = await fetch(`/api/news/${news.id}`);
      if (!response.ok) {
        // Se n√£o encontrar pelo ID, usar os dados j√° dispon√≠veis
        return news;
      }
      return response.json();
    },
    enabled: !!news?.id && isOpen,
    retry: false,
    staleTime: 1000 * 60 * 5 // 5 minutos
  });

  const currentNews = fullNews || news;

  if (!news) return null;

  const formatDate = (dateString: string) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('pt-BR', {
        day: 'numeric',
        month: 'long',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return dateString;
    }
  };

  const handleShare = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: news.title,
          text: news.description,
          url: news.link
        });
      } catch (err) {
        // Fallback para copiar para o clipboard
        copyToClipboard();
      }
    } else {
      copyToClipboard();
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(news.link);
    toast({
      title: "Link copiado!",
      description: "O link da not√≠cia foi copiado para a √°rea de transfer√™ncia."
    });
  };

  const handleBookmark = () => {
    setIsBookmarked(!isBookmarked);
    toast({
      title: isBookmarked ? "Marcador removido" : "Not√≠cia salva!",
      description: isBookmarked ? "A not√≠cia foi removida dos seus salvos." : "A not√≠cia foi salva na sua lista de leitura."
    });
  };

  const openOriginalArticle = () => {
    const link = currentNews?.link || news?.link;
    if (link && link !== '#') {
      window.open(link, '_blank', 'noopener,noreferrer');
    } else {
      toast({
        title: "Link n√£o dispon√≠vel",
        description: "Esta not√≠cia n√£o possui um link externo."
      });
    }
  };

  // Fun√ß√£o para limpar e melhorar o conte√∫do HTML
  const cleanHtmlContent = (htmlContent: string) => {
    if (!htmlContent) return '';

    return htmlContent
      .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '') // Remove scripts
      .replace(/<style\b[^<]*(?:(?!<\/style>)<[^<]*)*<\/style>/gi, '') // Remove styles
      .replace(/<noscript\b[^<]*(?:(?!<\/noscript>)<[^<]*)*<\/noscript>/gi, '') // Remove noscript
      .replace(/<!--[\s\S]*?-->/g, '') // Remove coment√°rios HTML
      .replace(/style="[^"]*"/gi, '') // Remove inline styles
      .replace(/class="[^"]*"/gi, '') // Remove classes
      .replace(/onclick="[^"]*"/gi, '') // Remove onclick events
      .replace(/onload="[^"]*"/gi, '') // Remove onload events
      .replace(/<img([^>]*)>/gi, '<img$1 class="max-w-full h-auto rounded-lg my-4" loading="lazy" style="max-height: 300px; object-fit: cover;">') // Melhora imagens
      .replace(/<a([^>]*)>/gi, '<a$1 class="text-purple-400 hover:underline" target="_blank" rel="noopener noreferrer">') // Melhora links
      .replace(/<p>/gi, '<p class="mb-3 text-sm">') // Espa√ßamento de par√°grafos
      .replace(/<h([1-6])>/gi, '<h$1 class="font-semibold mt-4 mb-2 text-base">') // Estilos de t√≠tulos
      .replace(/<div>/gi, '<div class="mb-2">') // Espa√ßamento de divs
      .trim();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-hidden flex flex-col">
        <DialogHeader className="flex-shrink-0">
          <div className="flex items-start justify-between gap-4">
            <DialogTitle className="text-xl md:text-2xl leading-tight pr-8">
              {news.title}
            </DialogTitle>
            <Button
              variant="ghost"
              size="icon"
              onClick={onClose}
              className="flex-shrink-0"
              data-testid="button-close-news-modal"
            >
              <X className="w-4 h-4" />
            </Button>
          </div>

          {/* Meta informa√ß√µes */}
          <div className="flex flex-wrap items-center gap-4 text-sm text-muted-foreground mt-4">
            <div className="flex items-center gap-1">
              <Calendar className="w-4 h-4" />
              <span>{formatDate(news.publishedDate)}</span>
            </div>
            {news.author && (
              <div className="flex items-center gap-1">
                <User className="w-4 h-4" />
                <span>{news.author}</span>
              </div>
            )}
            {news.category && (
              <Badge variant="secondary" className="text-xs">
                {news.category}
              </Badge>
            )}
          </div>
        </DialogHeader>

        <Separator className="my-4" />

        {/* Imagem da not√≠cia */}
        {news.thumbnail && (
          <div className="flex-shrink-0 mb-4">
            <img
              src={news.thumbnail}
              alt={news.title}
              className="w-full max-h-64 object-cover rounded-lg"
              data-testid="img-news-modal-thumbnail"
            />
          </div>
        )}

        {/* Conte√∫do da not√≠cia */}
        <ScrollArea className="flex-1 pr-4">
          {loadingFullNews ? (
            <div className="space-y-4">
              <Skeleton className="h-4 w-full" />
              <Skeleton className="h-4 w-3/4" />
              <Skeleton className="h-4 w-5/6" />
              <div className="flex items-center gap-2 mt-4">
                <Loader2 className="w-4 h-4 animate-spin" />
                <span className="text-sm text-muted-foreground">Carregando conte√∫do completo...</span>
              </div>
            </div>
          ) : (
            <div className="space-y-4">
              {/* Descri√ß√£o */}
              <div className="text-muted-foreground leading-relaxed mb-4">
                {currentNews?.description}
              </div>

              {/* Conte√∫do completo da not√≠cia */}
              {currentNews?.content && currentNews.content.length > currentNews.description.length ? (
                <div className="prose prose-sm dark:prose-invert max-w-none">
                  <Separator className="my-4" />
                  <h4 className="font-semibold mb-3">Conte√∫do Completo:</h4>
                  <div 
                    className="text-sm leading-relaxed space-y-3 max-h-96 overflow-y-auto pr-2"
                    dangerouslySetInnerHTML={{ 
                      __html: cleanHtmlContent(currentNews.content) 
                    }}
                  />
                </div>
              ) : (
                <div className="bg-blue-500/10 border border-blue-500/20 rounded-lg p-4 mt-4">
                  <h4 className="font-semibold mb-2 text-blue-400">Conte√∫do da Not√≠cia:</h4>
                  <div className="text-sm text-muted-foreground space-y-2">
                    <div>Esta √© uma not√≠cia externa do Anime News Network.</div>
                    <div className="text-xs">O conte√∫do mostrado √© o resumo dispon√≠vel no feed RSS. Para ler o artigo completo, clique no bot√£o "Ler no site original" abaixo.</div>
                    {loadingFullNews && (
                      <div className="flex items-center gap-2 text-blue-400">
                        <div className="animate-spin rounded-full h-3 w-3 border-b border-blue-400"></div>
                        <span className="text-xs">Tentando carregar conte√∫do completo...</span>
                      </div>
                    )}
                  </div>
                </div>
              )}

              {/* Informa√ß√µes da not√≠cia */}
              <div className="bg-muted/50 rounded-lg p-4 mt-6">
                <h4 className="font-semibold mb-2">Informa√ß√µes da Not√≠cia:</h4>
                <ul className="space-y-1 text-muted-foreground text-sm">
                  <li>‚Ä¢ Fonte: {currentNews?.author || 'Anime News Network'}</li>
                  <li>‚Ä¢ Categoria: {currentNews?.category || 'Geral'}</li>
                  <li>‚Ä¢ Publicado em: {formatDate(currentNews?.publishedDate || '')}</li>
                  {currentNews?.link && currentNews.link !== '#' && (
                    <li>‚Ä¢ Link original: <a href={currentNews.link} target="_blank" rel="noopener noreferrer" className="text-purple-400 hover:underline">Ver no ANN</a></li>
                  )}
                </ul>
              </div>
            </div>
          )}
        </ScrollArea>

        <Separator className="my-4" />

        {/* A√ß√µes */}
        <div className="flex-shrink-0 flex flex-wrap gap-2 justify-between">
          <div className="flex gap-2">
            <Button
              onClick={handleBookmark}
              variant={isBookmarked ? "default" : "outline"}
              size="sm"
              className="flex items-center gap-2"
              data-testid="button-bookmark-news"
            >
              <BookmarkPlus className="w-4 h-4" />
              {isBookmarked ? "Salvo" : "Salvar"}
            </Button>

            <Button
              onClick={handleShare}
              variant="outline"
              size="sm"
              className="flex items-center gap-2"
              data-testid="button-share-news"
            >
              <Share2 className="w-4 h-4" />
              Compartilhar
            </Button>
          </div>

          {currentNews?.link && currentNews.link !== '#' && (
            <Button
              onClick={openOriginalArticle}
              className="flex items-center gap-2 bg-purple-600 hover:bg-purple-700"
              data-testid="button-read-full-article"
            >
              <ExternalLink className="w-4 h-4" />
              Ler no site original
            </Button>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ProfileImageUpload.content:
import { useState, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Camera, Upload, User, X } from "lucide-react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";

interface ProfileImageUploadProps {
  currentImage?: string;
  userName: string;
  size?: "sm" | "md" | "lg";
}

export default function ProfileImageUpload({ 
  currentImage, 
  userName, 
  size = "lg" 
}: ProfileImageUploadProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const sizeClasses = {
    sm: "w-8 h-8",
    md: "w-12 h-12", 
    lg: "w-20 h-20"
  };

  // Fun√ß√£o para redimensionar imagem
  const resizeImage = (file: File, maxWidth: number, maxHeight: number, quality: number = 0.8): Promise<Blob> => {
    return new Promise((resolve) => {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!;
      const img = new Image();

      img.onload = () => {
        // Calcular dimens√µes mantendo propor√ß√£o
        let { width, height } = img;

        if (width > height) {
          if (width > maxWidth) {
            height = (height * maxWidth) / width;
            width = maxWidth;
          }
        } else {
          if (height > maxHeight) {
            width = (width * maxHeight) / height;
            height = maxHeight;
          }
        }

        canvas.width = width;
        canvas.height = height;

        // Desenhar imagem redimensionada
        ctx.drawImage(img, 0, 0, width, height);

        // Converter para blob
        canvas.toBlob((blob) => {
          if (blob) {
            resolve(blob);
          }
        }, 'image/jpeg', quality);
      };

      img.src = URL.createObjectURL(file);
    });
  };

  // Mutation para upload da imagem
  const uploadMutation = useMutation({
    mutationFn: async (file: File) => {
      // Primeiro, redimensionar a imagem para 200x200px
      const resizedBlob = await resizeImage(file, 200, 200, 0.8);

      // Obter URL de upload
      const uploadResponse = await apiRequest("POST", "/api/profile/upload-url");
      const { uploadURL } = await uploadResponse.json();

      // Upload da imagem redimensionada
      const uploadResult = await fetch(uploadURL, {
        method: 'PUT',
        body: resizedBlob,
        headers: {
          'Content-Type': 'image/jpeg',
        },
      });

      if (!uploadResult.ok) {
        throw new Error('Falha no upload da imagem');
      }

      // Atualizar perfil com nova imagem
      const updateResponse = await apiRequest("PUT", "/api/profile/avatar", {
        avatarUrl: uploadURL.split('?')[0], // Remove query parameters
      });

      return await updateResponse.json();
    },
    onSuccess: () => {
      toast({
        title: "Foto atualizada!",
        description: "Sua foto de perfil foi atualizada com sucesso.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      setIsOpen(false);
      setSelectedFile(null);
      setPreviewUrl(null);
    },
    onError: (error: any) => {
      toast({
        title: "Erro no upload",
        description: error.message || "Falha ao atualizar foto de perfil.",
        variant: "destructive",
      });
    },
  });

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Validar tipo de arquivo
    if (!file.type.startsWith('image/')) {
      toast({
        title: "Arquivo inv√°lido",
        description: "Por favor, selecione uma imagem v√°lida.",
        variant: "destructive",
      });
      return;
    }

    // Validar tamanho (max 5MB)
    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: "Arquivo muito grande",
        description: "A imagem deve ter no m√°ximo 5MB.",
        variant: "destructive",
      });
      return;
    }

    setSelectedFile(file);

    // Criar preview
    const reader = new FileReader();
    reader.onload = (e) => {
      setPreviewUrl(e.target?.result as string);
    };
    reader.readAsDataURL(file);
  };

  const handleUpload = () => {
    if (selectedFile) {
      uploadMutation.mutate(selectedFile);
    }
  };

  const handleRemoveSelection = () => {
    setSelectedFile(null);
    setPreviewUrl(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <div className="relative group cursor-pointer">
          <Avatar className={`${sizeClasses[size]} ring-2 ring-primary/20 group-hover:ring-primary/40 transition-all`}>
            <AvatarImage src={currentImage} alt={userName} />
            <AvatarFallback>
              <User className="w-1/2 h-1/2" />
            </AvatarFallback>
          </Avatar>
          <div className="absolute inset-0 bg-black/50 rounded-full opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
            <Camera className="w-1/3 h-1/3 text-white" />
          </div>
        </div>
      </DialogTrigger>

      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>Alterar Foto de Perfil</DialogTitle>
          <DialogDescription>
            Escolha uma nova foto para seu perfil. A imagem ser√° redimensionada automaticamente para 200x200px.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {/* Preview da imagem atual ou selecionada */}
          <div className="flex justify-center">
            <Avatar className="w-32 h-32 ring-2 ring-border">
              <AvatarImage src={previewUrl || currentImage} alt={userName} />
              <AvatarFallback>
                <User className="w-16 h-16" />
              </AvatarFallback>
            </Avatar>
          </div>

          {/* Input de arquivo */}
          <input
            ref={fileInputRef}
            type="file"
            accept="image/*"
            onChange={handleFileSelect}
            className="hidden"
          />

          {/* Bot√µes de a√ß√£o */}
          <div className="space-y-2">
            {!selectedFile ? (
              <Button
                onClick={() => fileInputRef.current?.click()}
                className="w-full"
                variant="outline"
                data-testid="button-select-image"
              >
                <Upload className="w-4 h-4 mr-2" />
                Selecionar Imagem
              </Button>
            ) : (
              <div className="space-y-2">
                <div className="flex gap-2">
                  <Button
                    onClick={handleUpload}
                    disabled={uploadMutation.isPending}
                    className="flex-1"
                    data-testid="button-upload-image"
                  >
                    {uploadMutation.isPending ? (
                      <div className="flex items-center gap-2">
                        <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                        Carregando...
                      </div>
                    ) : (
                      <>
                        <Upload className="w-4 h-4 mr-2" />
                        Salvar Foto
                      </>
                    )}
                  </Button>
                  <Button
                    onClick={handleRemoveSelection}
                    variant="outline"
                    size="icon"
                    data-testid="button-remove-selection"
                  >
                    <X className="w-4 h-4" />
                  </Button>
                </div>
                <p className="text-xs text-muted-foreground text-center">
                  {selectedFile.name} ({(selectedFile.size / 1024 / 1024).toFixed(2)}MB)
                </p>
              </div>
            )}
          </div>

          {/* Informa√ß√µes */}
          <div className="text-xs text-muted-foreground space-y-1">
            <p>‚Ä¢ Formatos aceitos: JPG, PNG, GIF</p>
            <p>‚Ä¢ Tamanho m√°ximo: 5MB</p>
            <p>‚Ä¢ A imagem ser√° redimensionada automaticamente</p>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/hooks/useAnimeCompletion.ts.content:
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface CompleteAnimeData {
  animeId: string;
  animeTitle: string;
  animeImage?: string;
  totalEpisodes: number;
}

export function useAnimeCompletion() {
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const completeAnimeMutation = useMutation({
    mutationFn: async (data: CompleteAnimeData) => {
      const response = await apiRequest("POST", "/api/anime/complete", data);
      return await response.json();
    },
    onSuccess: (data, variables) => {
      toast({
        title: "üéâ Anime Completado!",
        description: `Parab√©ns! Voc√™ completou "${variables.animeTitle}" e ganhou ${variables.totalEpisodes * 10} pontos!`,
      });

      // Invalidar queries relacionadas
      queryClient.invalidateQueries({ queryKey: ["/api/auth/stats"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/completed-animes"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/progress"] });
    },
    onError: (error: any) => {
      toast({
        title: "Erro ao completar anime",
        description: error.message || "N√£o foi poss√≠vel marcar o anime como completado.",
        variant: "destructive",
      });
    },
  });

  // Fun√ß√£o para verificar se um anime deve ser marcado como completado
  const checkAndCompleteAnime = (
    animeId: string,
    episodeNumber: number,
    totalEpisodes: number,
    animeTitle: string,
    animeImage?: string
  ) => {
    if (episodeNumber >= totalEpisodes && totalEpisodes > 0) {
      completeAnimeMutation.mutate({
        animeId: animeId.toString(),
        animeTitle,
        animeImage,
        totalEpisodes,
      });
      return true;
    }
    return false;
  };

  return {
    completeAnime: completeAnimeMutation.mutate,
    checkAndCompleteAnime,
    isCompleting: completeAnimeMutation.isPending,
  };
}

