// AniList GraphQL API Integration - API principal para dados de anime/manga
import type { Anime, Manga } from "@shared/schema";

// Base URL da AniList GraphQL API
const ANILIST_API_BASE = "https://graphql.anilist.co";

// Tipos espec√≠ficos da API AniList
interface AniListAnime {
  id: number;
  title: {
    romaji: string;
    english?: string;
    native?: string;
  };
  coverImage: {
    large: string;
    medium: string;
    color?: string;
  };
  bannerImage?: string;
  description?: string;
  episodes?: number;
  duration?: number;
  status: string;
  startDate?: {
    year?: number;
    month?: number;
    day?: number;
  };
  endDate?: {
    year?: number;
    month?: number;
    day?: number;
  };
  season?: string;
  seasonYear?: number;
  genres?: string[];
  studios?: {
    nodes: Array<{
      name: string;
      isAnimationStudio: boolean;
    }>;
  };
  averageScore?: number;
  meanScore?: number;
  popularity?: number;
  favourites?: number;
  format?: string;
  source?: string;
  isAdult?: boolean;
  trending?: number;
  nextAiringEpisode?: {
    episode: number;
    timeUntilAiring: number;
  };
}

interface AniListManga {
  id: number;
  title: {
    romaji: string;
    english?: string;
    native?: string;
  };
  coverImage: {
    large: string;
    medium: string;
    color?: string;
  };
  bannerImage?: string;
  description?: string;
  chapters?: number;
  volumes?: number;
  status: string;
  startDate?: {
    year?: number;
    month?: number;
    day?: number;
  };
  endDate?: {
    year?: number;
    month?: number;
    day?: number;
  };
  genres?: string[];
  averageScore?: number;
  meanScore?: number;
  popularity?: number;
  favourites?: number;
  format?: string;
  source?: string;
  isAdult?: boolean;
  staff?: {
    nodes: Array<{
      name: {
        full: string;
      };
    }>;
  };
}

interface AniListResponse<T> {
  data: T;
  errors?: Array<{
    message: string;
    status?: number;
  }>;
}

// Fun√ß√£o auxiliar para fazer requisi√ß√µes GraphQL
async function anilistRequest<T>(query: string, variables: any = {}): Promise<T> {
  try {
    const response = await fetch(ANILIST_API_BASE, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
        'User-Agent': 'AnimePulse/1.0'
      },
      body: JSON.stringify({
        query,
        variables
      })
    });

    if (!response.ok) {
      throw new Error(`AniList API error: ${response.status} ${response.statusText}`);
    }

    const result: AniListResponse<T> = await response.json();

    if (result.errors) {
      throw new Error(`AniList GraphQL error: ${result.errors.map(e => e.message).join(', ')}`);
    }

    // Rate limiting preventivo (1 requisi√ß√£o por segundo)
    await new Promise(resolve => setTimeout(resolve, 1000));

    return result.data;
  } catch (error) {
    console.error(`Error fetching from AniList API:`, error);
    throw error;
  }
}

// GraphQL Queries
const TRENDING_ANIME_QUERY = `
  query ($page: Int, $perPage: Int) {
